import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}));

export default function SimpleSelect({ pos }) {
  const classes = useStyles();
  const [position, setPosition] = React.useState("");

  const handleChange = (event) => {
    pos = event.target.value;
    // setPosition(event.target.value);
  };

  return (
    <div>
      <FormControl className={classes.formControl}>
        <InputLabel id="simple-select-label">Position</InputLabel>
        <Select value={pos} onChange={handleChange}>
          <MenuItem value={"Setter"}>Setter</MenuItem>
          <MenuItem value={"Opposite"}>Opposite</MenuItem>
          <MenuItem value={"Middle-Blocker"}>Middle-Blocker</MenuItem>
        </Select>
      </FormControl>
    </div>
  );
}
