import React from "react";
import Button from "components/Button";
import Link from "components/Link";
import styled from "styled-components";

import MaterialTable from "material-table";

export default function TeamListView({ teams = [] }) {
  const columns = [{ title: "Name", field: "name" }];
  return (
    <DataTypesPageStyled>
      <DataTypesListStyled>
        <MaterialTable
          title="Teams"
          columns={columns}
          data={teams}
          editable={{
            onRowUpdate: (newData, oldData) =>
              new Promise((resolve) => {
                setTimeout(() => {
                  resolve();
                  if (oldData) {
                    onDataTypeEdit(newData);
                  }
                }, 600);
              }),
          }}
        />
        <Link to="/data-types/create">
          <Button
            color="primary"
            variant="contained"
            type="submit"
            data-testid="sign-up-button"
          >
            Create New DataType
          </Button>
        </Link>
      </DataTypesListStyled>
    </DataTypesPageStyled>
  );
}

const DataTypesPageStyled = styled.div`
  min-height: 100vh;
  background-color: ${(props) => props.theme.palette.auth.background};
`;

const DataTypesListStyled = styled.div`
  display: grid;
  gap: 20px;
  text-align: right;
  padding: 50px;
  button {
    height: 50px;
    width: 250px;
    padding: 20px;
  }
`;
