import React from "react";
import Button from "../button.component";
import { Link } from "react-router-dom";
import styled from "styled-components";

import PropTypes from "prop-types";
import Edit from "@material-ui/icons/Edit";
// import { LockIcon } from "@material-ui/icons";
import MaterialTable from "material-table";

export default function TeamListView({ players = [] }) {
  const columns = [
    { title: "Name", field: "name" },
    { title: "Position", field: "position" },
    { title: "Number", field: "number" },
    { title: "Height", field: "height" },
    { title: "Weight", field: "weight" },
  ];

  return (
    <PlayersPageStyled>
      <PlayersListStyled>
        <MaterialTable
          title="Teams"
          columns={columns}
          localization={{
            pagination: {
              labelDisplayedRows: "{from}-{to} of {count}",
            },
            toolbar: {
              nRowsSelected: "{0} row(s) selected",
            },
            header: {
              actions: "Actions",
            },
            body: {
              emptyDataSourceMessage: "No records to display",
              filterRow: {
                filterTooltip: "Filter",
              },
            },
          }}
          // actions={[
          //   {
          //     icon: Edit,
          //     tooltip: "Edit User",
          //     onClick: (event, rowData) =>
          //       alert("You are editing " + rowData.name),
          //   },
          //   // {
          //   //   icon: "delete",
          //   //   tooltip: "Delete User",
          //   //   // onClick: (event, rowData) => confirm('You want to delete ' + rowData.name)
          //   // },
          // ]}
          data={players}
          editable={{
            onRowUpdate: (newData, oldData) =>
              new Promise((resolve) => {
                setTimeout(() => {
                  resolve();
                  if (oldData) {
                    // onDataTypeEdit(newData);
                  }
                }, 600);
              }),
          }}
        />
        <Link to="/create_team">
          <Button
            color="primary"
            variant="contained"
            type="submit"
            data-testid="sign-up-button"
          >
            Create New Player
          </Button>
        </Link>
      </PlayersListStyled>
    </PlayersPageStyled>
  );
}

const PlayersPageStyled = styled.div`
  min-height: 100vh;
`;

const PlayersListStyled = styled.div`
  display: grid;
  gap: 20px;
  text-align: right;
  padding: 50px;
  button {
    height: 50px;
    width: 250px;
    padding: 20px;
  }
`;
