import axios from "axios";

const API_URL = "http://localhost:8080/api/team/";

export const buildTokenAuthHeader = (token) => {
  return {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };
};

class TeamService {
  createTeam(id, name, token) {
    return axios
      .post(API_URL + id, { name }, buildTokenAuthHeader(token))
      .then((response) => {
        return response.data;
      });
  }
}

export default new TeamService();
