import React, { Component } from "react";
import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import CheckButton from "react-validation/build/button";
import { Segment, Checkbox } from "semantic-ui-react";

import { isEmail } from "validator";

import { connect } from "react-redux";
import { register } from "../actions/auth";
import { clearMessage } from "../actions/message";

const required = (value) => {
  if (!value) {
    return (
      <div className="alert alert-danger" role="alert">
        This field is required!
      </div>
    );
  }
};

const username = (value) => {
  if (!isEmail(value)) {
    return (
      <div className="alert alert-danger" role="alert">
        This is not a valid username.
      </div>
    );
  }
};

const vname = (value) => {
  if (value.length < 3 || value.length > 20) {
    return (
      <div className="alert alert-danger" role="alert">
        The name must be between 3 and 20 characters.
      </div>
    );
  }
};

const vpassword = (value) => {
  if (value.length < 8 || value.length > 40) {
    return (
      <div className="alert alert-danger" role="alert">
        The password must be between 8 and 40 characters.
      </div>
    );
  }
};

export const roles = {
  ADMIN: "ROLE_ADMIN",
  TEACHER: "ROLE_TEACHER",
  STUDENT: "ROLE_STUDENT",
};

const MyRolesInput = ({ state, ...props }) => {
  console.log(state, props);
  return (
    <Segment inverted>
      <Segment compact inverted>
        <Checkbox
          inverted
          id="role-Admin"
          checked={state.roles.includes(roles.ADMIN)}
          label="Admin"
          onChange={(event) => {
            if (!event.target.checked) {
              state.setFieldValue(
                "roles",
                state.roles.filter((r) => r !== roles.ADMIN)
              );
            } else {
              state.setFieldValue("roles", [roles.ADMIN].concat(state.roles));
            }
          }}
        />
      </Segment>
      <Segment compact inverted>
        <Checkbox
          inverted
          id="role-Teacher"
          checked={state.roles.includes(roles.TEACHER)}
          label="Teacher"
          onChange={(event) => {
            if (!event.target.checked) {
              state.setFieldValue(
                "roles",
                state.roles.filter((r) => r !== roles.TEACHER)
              );
            } else {
              state.setFieldValue("roles", [roles.TEACHER].concat(state.roles));
            }
          }}
        />
      </Segment>
      <Segment compact inverted>
        <Checkbox
          inverted
          id="role-Student"
          checked={state.roles.includes(roles.STUDENT)}
          label="Student"
          onChange={(event) => {
            if (!event.target.checked) {
              state.setFieldValue(
                "roles",
                state.roles.filter((r) => r !== roles.STUDENT)
              );
            } else {
              state.setFieldValue("roles", [roles.STUDENT].concat(state.roles));
            }
          }}
        />
      </Segment>
    </Segment>
  );
};

class Register extends Component {
  constructor(props) {
    super(props);
    this.handleRegister = this.handleRegister.bind(this);
    this.onChangeName = this.onChangeName.bind(this);
    this.onChangeUsername = this.onChangeUsername.bind(this);
    this.onChangePassword = this.onChangePassword.bind(this);

    this.state = {
      name: "",
      username: "",
      password: "",
      successful: false,
    };
  }

  componentDidMount() {
    this.props.dispatch(clearMessage);
  }

  onChangeName(e) {
    this.setState({
      name: e.target.value,
    });
  }

  onChangeUsername(e) {
    this.setState({
      username: e.target.value,
    });
  }

  onChangePassword(e) {
    this.setState({
      password: e.target.value,
    });
  }

  handleRegister(e) {
    e.preventDefault();

    this.setState({
      successful: false,
    });

    this.form.validateAll();

    if (this.checkBtn.context._errors.length === 0) {
      this.props
        .dispatch(
          register(this.state.name, this.state.username, this.state.password)
        )
        .then(() => {
          this.setState({
            successful: true,
          });
        })
        .catch(() => {
          this.setState({
            successful: false,
          });
        });
    }
  }

  render() {
    const { message } = this.props;
    console.log(message);

    return (
      <div className="col-md-12">
        <div className="card card-container">
          <img
            src="//ssl.gstatic.com/accounts/ui/avatar_2x.png"
            alt="profile-img"
            className="profile-img-card"
          />

          <Form
            onSubmit={this.handleRegister}
            ref={(c) => {
              this.form = c;
            }}
          >
            {!this.state.successful && (
              <div>
                <div className="form-group">
                  <label htmlFor="name">Name</label>
                  <Input
                    type="text"
                    className="form-control"
                    name="name"
                    value={this.state.name}
                    onChange={this.onChangeName}
                    validations={[required, vname]}
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="username">Username</label>
                  <Input
                    type="email"
                    className="form-control"
                    name="username"
                    value={this.state.username}
                    onChange={this.onChangeUsername}
                    validations={[required, username]}
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="password">Password</label>
                  <Input
                    type="password"
                    className="form-control"
                    name="password"
                    value={this.state.password}
                    onChange={this.onChangePassword}
                    validations={[required, vpassword]}
                  />
                </div>

                <div className="form-group">
                  <button className="btn btn-primary btn-block">Sign Up</button>
                  <MyRolesInput />
                </div>
              </div>
            )}

            {message && (
              <div className="form-group">
                <div
                  className={
                    this.state.successful
                      ? "alert alert-success"
                      : "alert alert-danger"
                  }
                  role="alert"
                >
                  {message}
                </div>
              </div>
            )}
            <CheckButton
              style={{ display: "none" }}
              ref={(c) => {
                this.checkBtn = c;
              }}
            />
          </Form>
        </div>
      </div>
    );
  }
}

function mapStateToProps(state) {
  const { message } = state.message;
  return {
    message,
  };
}

export default connect(mapStateToProps)(Register);
