import React, { Fragment } from "react";

import { MDBBtn, MDBTable, MDBTableBody, MDBTableHead } from "mdbreact";

const Profile = (props) => {
  const columns = [
    {
      label: "#",
      field: "id",
      sort: "asc",
    },
    {
      label: "First",
      field: "first",
      sort: "asc",
    },
    {
      label: "Last",
      field: "last",
      sort: "asc",
    },
    {
      label: "Handle",
      field: "handle",
      sort: "asc",
    },
  ];

  const rows_rounded_btn = [
    {
      id: 1,
      first: [
        <MDBBtn color="default" rounded size="sm">
          +
        </MDBBtn>,
        <Fragment background="blue">Points</Fragment>,
        <MDBBtn color="default" rounded size="sm">
          -
        </MDBBtn>,
      ],
      last: "Otto",
      handle: "@mdo",
    },
    {
      id: 2,
      first: "Jacob",
      last: (
        <MDBBtn color="default" rounded size="sm">
          Button
        </MDBBtn>
      ),
      handle: "@fat",
    },
    {
      id: 3,
      first: "Larry",
      last: "the Bird",
      handle: (
        <MDBBtn color="default" rounded size="sm">
          Button
        </MDBBtn>
      ),
    },
  ];

  return [
    <Fragment>Players Statistics</Fragment>,
    <MDBTable btn>
      <MDBTableHead columns={columns} />
      <MDBTableBody rows={rows_rounded_btn} />
    </MDBTable>,
    <Fragment>Overall Statistics</Fragment>,
    <MDBTable btn>
      <MDBTableHead columns={columns} />
      <MDBTableBody rows={rows_rounded_btn} />
    </MDBTable>,
  ];
};

export default Profile;
