import axios from "axios";
import match from "../reducers/match";

const API_URL = "http://localhost:8080/api/user/";

export const buildTokenAuthHeader = (header) => {
  return {
    headers: {
      Authorization: `Bearer ${{ "x-access-token": header.token }}`,
    },
  };
};

class MatchService {
  createTeam(name, teamId, header) {
    // console.log(name);
    // console.log(header);
    return axios
      .post(
        API_URL + header.id + "/team/" + teamId,
        { name, header },
        buildTokenAuthHeader(header)
      )
      .then((response) => {
        return response.data;
      });
  }

  //   getTeamByUserId(header) {
