export const userConstraints = {
  PASSWORD_MIN_LENGTH: 8,
  USERNAME_MAX_LENGTH: 15,
  PASSWORD_PATTERN: /^(?=.*[0-9])(?=.*[!@#$%^&*])[a-zA-Z0-9!@#$%^&*]{8,}$/,
};

export const roles = {
  ADMIN: "ROLE_ADMIN",
  TEACHER: "ROLE_TEACHER",
  STUDENT: "ROLE_STUDENT",
};

