const MyRolesInput = ({ field, form, ...props }) => {
  console.log(field, form, props);
  return (
    <Segment inverted>
      <Segment compact inverted>
        <Checkbox
          inverted
          id="role-Admin"
          checked={form.values.roles.includes(roles.ADMIN)}
          label="Admin"
          onChange={(event) => {
            if (!event.target.checked) {
              form.setFieldValue(
                "roles",
                form.values.roles.filter((r) => r !== roles.ADMIN)
              );
            } else {
              form.setFieldValue(
                "roles",
                [roles.ADMIN].concat(form.values.roles)
              );
            }
          }}
        />
      </Segment>
      <Segment compact inverted>
