export const Register = () => {
  const [errors, setErrors] = useState(null);
  const [serverErr, setServerErrors] = useState(null);

  const history = useHistory();
  // const dispatch = useDispatch();

  const handleRegister = async (data) => {
    try {
      const response = await register(
        data.firstName,
        data.username,
        data.password
      );
      console.log("REGISTER SUCCESS: ", response);
      history.push("/auth/login");
    } catch (err) {
      console.log("REGISTER ERROR: ", err);
      if (err.errors) {
        setErrors(err.errors);
      } else {
        setServerErrors(err.message);
      }
    }
  };

  return (
    <Segment inverted>
      {serverErr && (
        <Message negative>
          <Message.Header>{serverErr}</Message.Header>
        </Message>
      )}
      <Formik
        initialValues={{
          // Once the username is set wont be able to change
          id: null,
          username: "",
          password: "",
          firstName: "",
          lastName: "",
          roles: [],
        }}
        onSubmit={(values, actions) => {
          handleRegister(values);
        }}
      >
        {(props) => {
          // console.log(props);
          return (
            <Grid textAlign="center">
              <Grid.Column>
                <h1 style={{ color: "white" }}>Register</h1>
                <Form
                  style={{
                    width: "50%",
                    margin: "auto",
                  }}
                >
                  <FormSemantic inverted>
                    <Field
                      name={"firstName"}
                      component={MyFirstNameInput}
                      errors={errors ? errors["firstName"] : null}
                    />
                    <Field
                      name={"lastName"}
                      component={MyLastNameInput}
                      errors={errors ? errors["lastName"] : null}
                    />
                    <Field
                      name={"username"}
                      component={MyUsernameInput}
                      errors={errors ? errors["username"] : null}
                      validate={validateUsername}
                    />
                    <Field
                      name={"password"}
                      component={MyPasswordInput}
                      errors={errors ? errors["password"] : null}
                    />
                    <Field name={"roles"} component={MyRolesInput} />
                    <Button type={"submit"} color={"blue"}>
                      Sing up
                    </Button>
                  </FormSemantic>
                </Form>
              </Grid.Column>
            </Grid>
          );
        }}
      </Formik>
    </Segment>
  );
};
