import React, { useState, useEffect } from "react";
import authHeader from "../../services/auth-header";
import TeamListView from "./teamListView.component";
import { getTeamsById } from "../../actions/team";
import axios from "axios";

export function TeamsList(props) {
  const [teamsLoaded, setTeamsLoaded] = useState(false);
  const [teams, setTeams] = useState([]);
  const [error, setError] = useState(null);

  const logged = authHeader();
  console.log(logged);

  const setPeriodError = (error) => {
    setError(error);
    setTimeout(() => {
      setError(null);
    }, 4000);
  };
  const loadTeams = async () => {
    try {
      if (!teamsLoaded) {
        setTeamsLoaded(true);
        const config = {
          headers: { Authorization: `Bearer ${logged.token}` },
        };
        const response = await axios.get(
          "http://localhost:8080/api/team/user/" + logged.id,
          config
        );
        // console.log(response);
        console.log(response.data);
        setTeams(response.data);
      }
    } catch (err) {
      setPeriodError(err);
      setTeamsLoaded(false);
    }
  };
  loadTeams();

  return <TeamListView {...props} teams={teams} />;
}

