import React, { Fragment } from "react";
import { Header } from "semantic-ui-react";
import { MDBBtn, MDBTable, MDBTableBody, MDBTableHead } from "mdbreact";

class Profile extends Component {
  constructor(props) {
    super(props);

    this.state = {
      allyPoints: 0,
      oppPoints: 0,
      allyGames: 0,
      oppGames: 0,
      isFinished: false,
    };
  }

  render() {
    const columns = [
      {
        label: "Name",
        field: "name",
        sort: "asc",
      },
      {
        label: "Spikes",
        field: "spikes",
        sort: "asc",
      },
      {
        label: "Blocks",
        field: "blocks",
        sort: "asc",
      },
      {
        label: "SpikeErrors",
        field: "spikeErrors",
        sort: "asc",
      },
      {
        label: "blockErrors",
        field: "blockErrors",
        sort: "asc",
      },
      {
        label: "ServerErrors",
        field: "serveErrors",
        sort: "asc",
      },
      {
        label: "ReceiveErrors",
        field: "receiveErrors",
        sort: "asc",
      },
      {
        label: "Aces",
        field: "aces",
        sort: "asc",
      },
      {
        label: "Attempts",
        field: "attempts",
        sort: "asc",
      },
    ];

    const rows_rounded_btn = [
      {
        name: "Ivan",
        first: [
          <MDBBtn
            color="default"
            rounded
            size="sm"
            onClick={this.setState({
              allyPoints: this.state.allyPoints++,
            })}
          >
            +
          </MDBBtn>,
          <Fragment>{this.state.allyPoints}</Fragment>,
          <MDBBtn color="default" rounded size="sm">
            -
          </MDBBtn>,
        ],
        last: "Otto",
        handle: "@mdo",
      },
      {
        name: "Spas",
        first: "Jacob",
        last: (
          <MDBBtn color="default" rounded size="sm">
            Button
          </MDBBtn>
        ),
        handle: "@fat",
      },
      {
        name: "sder",
        first: "Larry",
        last: "the Bird",
        handle: (
          <MDBBtn color="default" rounded size="sm">
            Button
          </MDBBtn>
        ),
      },
    ];
    return [
      [
        // [<h2>Nie</h2>, <h1>2</h1>],
        <Header as="h1" floated="left">
          Nie 3 : 2 Te
        </Header>,
        <Header as="h1" floated="right">
          Overall Statistics
        </Header>,
      ],
      <Fragment>Players Statistics</Fragment>,
      <MDBTable btn>
        <MDBTableHead columns={columns} />
        <MDBTableBody rows={rows_rounded_btn} />
      </MDBTable>,
      <Fragment>Overall Statistics</Fragment>,
      <MDBTable btn>
        <MDBTableHead columns={columns} />
        <MDBTableBody rows={rows_rounded_btn} />
      </MDBTable>,
    ];
  }
}
export default Profile;
