import axios from "axios";

const API_URL = "http://localhost:8080/api/team/";

export const buildTokenAuthHeader = (token) => {
  return {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };
};

class TeamService {
  createTeam(id, token) {
    return axios
      .post(API_URL + id, { name }, buildTokenAuthHeader(token))
      .then((response) => {
        return response.data;
      });
  }

  logout() {
    localStorage.removeItem("user");
  }

  register(name, username, password, roles) {
    console.log("akjsdjkalsjfklajsdf");
    console.log(roles);
    return axios.post(API_URL + "signup", {
      name,
      username,
      password,
      roles,
    });
  }
}

export default new AuthService();
