import React, { Fragment } from "react";
import { Header } from "semantic-ui-react";
import { MDBBtn, MDBTable, MDBTableBody, MDBTableHead } from "mdbreact";
import axios from "axios";
import authHeader from "../services/auth-header";

class Profile extends Component {
  constructor(props) {
    super(props);

    this.state = {
      allyPoints: 0,
      oppPoints: 0,
      allyGames: 0,
      oppGames: 0,
      players: [],
      isFinished: false,
    };
  }

  async componentDidMount() {
    const logged = authHeader();

    try {
      // if (!playersLoaded) {
      // setPlayersLoaded(true);
      const config = {
        headers: { Authorization: `Bearer ${logged.token}` },
      };
      const matchId = JSON.parse(localStorage.getItem("matchId"));

      const response = await axios.get(
        "http://localhost:8080/api/match/" + matchId,
        config
      );
      // console.log(response);
      this.setState({
        players: response.data.players,
      });
      console.log(response.data);
      // setPlayers(response.data);
      // }
    } catch (err) {
      // setError(err);
      // setPlayersLoaded(false);
    }
  }

  increasePlayerStat = (index, stat) => {
    // updatePlayers = players[index][stat]++;
    let newArray = [...this.state.players];
    newArray[index][stat] = this.state.players[index][stat] + 1;
    console.log(newArray);
    this.setState((prev) => ({
      players: newArray,
    }));
  };

  increaseAllyPoints = () => {
    this.setState(
      (prev) => ({
        allyPoints: prev.allyPoints + 1,
      }),
      () => {
        console.log(this.state.allyPoints);
      }
    );
  };
  increaseOpponentPoints = () => {
    this.setState(
      (prev) => ({
        oppPoints: prev.oppPoints + 1,
      }),
      () => {
        console.log(this.state.oppPoints);
      }
    );
  };
  increaseAllyGames = () => {
    this.setState(
      (prev) => ({
        allyGames: prev.allyGames + 1,
      }),
      () => {
        console.log(this.state.allyGames);
      }
    );
  };
  increaseOpponentGames = () => {
    this.setState(
      (prev) => ({
        oppGames: prev.oppGames + 1,
      }),
      () => {
        console.log(this.state.oppGames);
      }
    );
  };
  decreaseAllyPoints = () => {
    this.setState(
      (prev) => ({
        allyPoints: prev.allyPoints - 1,
      }),
      () => {
        console.log(this.state.allyPoints);
      }
    );
  };
  decreaseOpponentPoints = () => {
    this.setState(
      (prev) => ({
        oppPoints: prev.oppPoints - 1,
      }),
      () => {
        console.log(this.state.oppPoints);
      }
    );
  };
  decreaseAllyGames = () => {
    this.setState(
      (prev) => ({
        allyGames: prev.allyGames - 1,
      }),
      () => {
        console.log(this.state.allyGames);
      }
    );
  };
  decreaseOpponentGames = () => {
    this.setState(
      (prev) => ({
        oppGames: prev.oppGames - 1,
      }),
      () => {
        console.log(this.state.oppGames);
      }
    );
  };

  render() {
    const columns = [
      {
        label: "Name",
        field: "name",
        sort: "asc",
      },
      {
        label: "Spikes",
        field: "spikes",
        sort: "asc",
      },
      {
        label: "Blocks",
        field: "blocks",
        sort: "asc",
      },
      {
        label: "SpikeErrors",
        field: "spikeErrors",
        sort: "asc",
      },
      {
        label: "blockErrors",
        field: "blockErrors",
        sort: "asc",
      },
      {
        label: "ServerErrors",
        field: "serveErrors",
        sort: "asc",
      },
      {
        label: "ReceiveErrors",
        field: "receiveErrors",
        sort: "asc",
      },
      {
        label: "Aces",
        field: "aces",
        sort: "asc",
      },
      {
        label: "Attempts",
        field: "attempts",
        sort: "asc",
      },
    ];
    let rows_rounded_btn = [];
    if (this.state.players.length > 0) {
      for (let i = 0; i < this.state.players.length; i++) {
        rows_rounded_btn.push({
          name: this.state.players[i]["name"],
          spikes: [
            <MDBBtn
              color="default"
              rounded
              size="sm"
              onClick={() => this.increaseAllyPoints()}
            >
              +
            </MDBBtn>,
            <Fragment>{this.state.allyPoints}</Fragment>,
            <MDBBtn
              color="default"
              rounded
              size="sm"
              onClick={() => this.decreaseAllyPoints()}
            >
              -
            </MDBBtn>,
          ],
          blocks: [
            <MDBBtn
              color="default"
              rounded
              size="sm"
              onClick={() => this.increaseAllyPoints()}
            >
              +
            </MDBBtn>,
            <Fragment>{this.state.allyPoints}</Fragment>,
            <MDBBtn
              color="default"
              rounded
              size="sm"
              onClick={() => this.decreaseAllyPoints()}
            >
              -
            </MDBBtn>,
          ],
          spikeErrors: [
            <MDBBtn
              color="default"
              rounded
              size="sm"
              onClick={() => this.increaseAllyPoints()}
            >
              +
            </MDBBtn>,
            <Fragment>{this.state.allyPoints}</Fragment>,
            <MDBBtn
              color="default"
              rounded
              size="sm"
              onClick={() => this.decreaseAllyPoints()}
            >
              -
            </MDBBtn>,
          ],
          spikeErrors: [
            <MDBBtn
              color="default"
              rounded
              size="sm"
              onClick={() => this.increaseAllyPoints()}
            >
              +
            </MDBBtn>,
            <Fragment>{this.state.allyPoints}</Fragment>,
            <MDBBtn
              color="default"
              rounded
              size="sm"
              onClick={() => this.decreaseAllyPoints()}
            >
              -
            </MDBBtn>,
          ],
        });
      }
    } else {
    }
    console.log(this.state.players);
    return [
      [
        // [<h2>Nie</h2>, <h1>2</h1>],
        <Header as="h1" floated="left">
          Nie {this.state.allyGames} : {this.state.oppGames} Te
        </Header>,
        <Header as="h1" floated="right">
          Overall Statistics
        </Header>,
      ],
      <Fragment>Players Statistics</Fragment>,
      <MDBTable btn>
        <MDBTableHead columns={columns} />
        <MDBTableBody rows={rows_rounded_btn} />
      </MDBTable>,
      <Fragment>Overall Statistics</Fragment>,
      <MDBTable btn>
        <MDBTableHead columns={columns} />
        <MDBTableBody rows={rows_rounded_btn} />
      </MDBTable>,
    ];
  }
}
export default Profile;
