import React, { Component } from "react";
import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import CheckButton from "react-validation/build/button";

import { makeStyles } from "@material-ui/core/styles";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";

import { connect } from "react-redux";
import { clearMessage } from "../../actions/message";
import { createTeam } from "../../actions/team";

import authHeader from "../../services/auth-header";
import { Link } from "react-router-dom";
import SimpleSelect from "./dropdown";

export const playerConstraints = {
  NUMBER_PATTERN: /^[0-9]\b]{1,2}$/,
};

const required = (value) => {
  if (!value) {
    return (
      <div className="alert alert-danger" role="alert">
        This field is required!
      </div>
    );
  }
};

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}));

const vname = (value) => {
  if (value.length < 3 || value.length > 20) {
    return (
      <div className="alert alert-danger" role="alert">
        The name must be between 3 and 20 characters.
      </div>
    );
  }
};

const vnumber = (value) => {
  if (!value.match(playerConstraints.NUMBER_PATTERN)) {
    return (
      <div className="alert alert-danger" role="alert">
        This field must be number.
      </div>
    );
  } else if (value.length < 1 || value.length > 2) {
    return (
      <div className="alert alert-danger" role="alert">
        The number must be with one or two digits.
      </div>
    );
  }
};

class CreatePlayer extends Component {
  constructor(props) {
    super(props);
    this.handleRegister = this.handleRegister.bind(this);
    this.onChangeName = this.onChangeName.bind(this);

    this.state = {
      name: "",
      position: "",
      token: authHeader(),
      successful: false,
    };
  }

  componentDidMount() {
    this.props.dispatch(clearMessage);
  }

  onChangeName(e) {
    this.setState({
      name: e.target.value,
    });
  }

  onChangePosition(e) {
    this.setState({
      position: e.target.value,
    });
  }
  handleRegister(e) {
    e.preventDefault();

    this.setState({
      successful: false,
    });

    this.form.validateAll();

    if (this.checkBtn.context._errors.length === 0) {
      this.props
        .dispatch(createTeam(this.state.name, this.state.position))
        .then(() => {
          this.setState({
            successful: true,
          });
        })
        .catch(() => {
          this.setState({
            successful: false,
          });
        });
    }
  }

  render() {
    const { message } = this.props;
    const classes = useStyles();

    return (
      <div className="col-md-12">
        <div className="card card-container">
          <img
            src="//ssl.gstatic.com/accounts/ui/avatar_2x.png"
            alt="profile-img"
            className="profile-img-card"
          />

          <Form
            onSubmit={this.handleRegister}
            ref={(c) => {
              this.form = c;
            }}
          >
            {!this.state.successful && (
              <div>
                <div className="form-group">
                  <label htmlFor="name">Name</label>
                  <Input
                    type="text"
                    className="form-control"
                    name="name"
                    value={this.state.name}
                    onChange={this.onChangeName}
                    validations={[required, vname]}
                  />
                </div>
                <div>
                  <FormControl className={classes.formControl}>
                    <InputLabel id="simple-select-label">Position</InputLabel>
                    <Select
                      value={this.state.position}
                      onChange={this.onChangePosition}
                    >
                      <MenuItem value={"Setter"}>Setter</MenuItem>
                      <MenuItem value={"Opposite"}>Opposite</MenuItem>
                      <MenuItem value={"Middle-Blocker"}>
                        Middle-Blocker
                      </MenuItem>
                    </Select>
                  </FormControl>
                </div>
                <div className="form-group">
                  <button className="btn btn-primary btn-block">
                    Create Team
                  </button>
                </div>
              </div>
            )}

            {message && (
              <div className="form-group">
                <div
                  className={
                    this.state.successful
                      ? "alert alert-success"
                      : "alert alert-danger"
                  }
                  role="alert"
                >
                  {message}
                </div>
              </div>
            )}
            <CheckButton
              style={{ display: "none" }}
              ref={(c) => {
                this.checkBtn = c;
              }}
            />
          </Form>
        </div>
      </div>
    );
  }
}

function mapStateToProps(state) {
  const { message } = state.message;
  console.log(message);
  return {
    message,
  };
}

export default connect(mapStateToProps)(CreatePlayer);
